import React, {useState, useContext} from "react";

/**
 * Notes:
 *  - Password as of right now has no value. Need to implement on server-side.
 *  - handleLogin() sets the state.
 */

class Login extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      room: '',
      password: '',
      isLoggedIn: false,
      studentsList: [],
    };
    this.handleRoomChange = this.handleRoomChange.bind(this);
    this.handlePasswordChange = this.handlePasswordChange.bind(this);
    this.handleLogin = this.handleLogin.bind(this);
  }

  handleRoomChange(event) {
    this.setState({room: event.target.value});
  }

  handlePasswordChange(event) {
    this.setState({password: event.target.value});
  }

  //(content-type=application/grpc,user-agent=grpc-go/1.24.0-dev,x-forwarded-host=localhost:2005,x-forwarded-for=127.0.0.1,grpcgateway-accept=*/*,
  // grpcgateway-content-type=application/json,grpcgateway-user-agent=curl/7.65.3)
  handleLogin(event) {
    fetch( "http://localhost:2005/StudentRecordsService/getPeriodRecords", {
      method: 'POST',
      mode: 'no-cors',
      headers: {
        'Content-Type': 'application/grpc',
      },
      body: JSON.stringify({
        room: this.state.room,
      })
    }).then(function(response) {
      console.log(response);
      return response.json();
    }).then(function(data) {
      console.log(data);
      console.log(data.status)
    });
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleLogin}>
        <label>
          Room:
          <input type="text" value={this.state.room} onChange={this.handleRoomChange} />
        </label>
        <br/>
        <label>
          Password:
          <input type="text" value={this.state.password} onChange={this.handlePasswordChange} />
        </label>
        <br/>
        <input type="submit" value="Submit" />
      </form>
    )
  }
}

export default Login;
