import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import Navbar from 'react-bootstrap/Navbar'
import * as serviceWorker from './serviceWorker';

class LoginComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            room: '',
            password: '',
        };
        this.handleRoomChange = this.handleRoomChange.bind(this);
        this.handlePasswordChange = this.handleRoomChange.bind(this);
        this.handleLogin = this.handleLogin.bind(this);
    }

    handleRoomChange(event) {
        this.setState({
            room: event.target.value,
            password: this.state.password
        });
    }

    handlePasswordChange(event) {
        this.setState({
            room: this.state.room,
            password: event.target.value
        })
    }

    handleLogin(event) {
        alert('lmao -> ' + this.state.room + " " + this.state.password);
        event.preventDefault()
    }

    render() {
        return (
            <body>
            <Navbar bg="dark" variant="dark">
                <Navbar.Brand> msjhs attendance </Navbar.Brand>
                <Navbar bg="dark" variant="dark">

                </Navbar>
            </Navbar>
            <form onSubmit={this.handleLogin}>
                <label>
                    Room:
                    <input type="text" value={this.state.room} onChange={this.handleRoomChange} />
                </label>
                <br/>
                <label>
                    Password:
                    <input type="text" value={this.state.password} onChange={this.handlePasswordChange} />
                </label>
                <br/>
                <input type="submit" value="Submit" />
            </form>
            </body>

        )
    }
}


ReactDOM.render(<LoginComponent />, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
